              Master: {
                size: 25,
                image: 'static/icons/dashboard/network/Master.svg',
              },
              PoA: {
                size: 20,
                image: 'static/icons/dashboard/network/PoA.svg',
              },
              PoW: {
                size: 20,
                image: 'static/icons/dashboard/network/PoW.svg',
              },
              PoS: {
                size: 20,
                image: 'static/icons/dashboard/network/PoS.svg',
              }
            },
            interaction: {
              // hover: true,
              zoomView: true,
              dragNodes: false,
              dragView: true,
              hideEdgesOnDrag: true,
              hoverConnectedEdges: false,
              selectConnectedEdges: false,
              tooltipDelay: 200
            },
            nodes: {
              brokenImage: 'static/icons/dashboard/network/Node.svg',
              size: 15,
              shape: 'image',
              image: 'static/icons/dashboard/network/Node.svg',
              //fixed: true,
              // scaling: {
              //   min: 10,
              //   max: 30,
              //   /*label: {
              //     //min: 8,
              //     //max: 30,
              //     drawThreshold: 8,
              //     //maxVisible: 20
              //   }*/
              //   label: false
              // },
            },
            edges: {
              width: 0.25,
              selectionWidth: 0.25,
              color: {inherit: true, highlight: '#00add9', hover: '#00add9'},
              smooth: {
                enabled: false,
                // type: 'continuous',
                // forceDirection: 'none',
                // roundness: 0
              }
            },
            physics: {
              solver: 'hierarchicalRepulsion',
              // enabled: true,
              adaptiveTimestep: true,
              // scaling: {
              //   min: 1,
              //   max: 15
              // },
              hierarchicalRepulsion: {
                nodeDistance: 100,
                // centralGravity: 5,
                springLength: 50,
                // springConstant: 0.01,
                damping: 1
              },
              // barnesHut: {
              //   gravitationalConstant: -5000,
              //   springLength: 80,
              //   springConstant: 0.09
              // },
              stabilization: {
                fit: false,
                iterations: 20
              },
              // minVelocity: 0.75
            },
            layout: {
              improvedLayout: false,
              randomSeed: 191006
            },
          }
        },
        TPS: 0,
        emission: 0,
        difficulty: 25,
        hashRate: 0,
        blockSize: 0,
        lastKBlockAgo: 0,
        pending: 0,
        microblocks: 0,
        blocks: 0,
        transactions: 0,
        masterNodes: 0,
        powNodes: 0,
        poaTeams: 0,
        poaNodes: 0,
      }
    },
    watch: {
      "network.nodes"(val) {
        _.assign(this.$data, {
          masterNodes: val.filter(n => n.group === 'Master').length,
          powNodes: val.filter(n => n.group === 'PoW').length,
          poaNodes: val.filter(n => n.group === 'PoA').length,
        })
      }
    },
    methods: {
      reload() {
        if(this.$root.ws.ready) {
          this.$root.ws.call('getStats').then( r => {
            _.assign(this.$data, _.pick( r, _.keys(this.$data)));
            _.assign(this.$data.network, r.topology);
            this.network.edges = _.flatten(this.network.edges)
          })
        } else {
          setTimeout(() => {
            this.reload()
          }, 500)
        }
      },

      addListeners(e) {
        if (e) {
          this.$root.ws.on('dashboard.stats', r => {
            _.assign(this.$data, _.pick(r, _.keys(this.$data)));
          });

          this.$root.ws.on('dashboard.addNode', r => {
            if (_.find(this.network.nodes, r.node)) return
            this.network.nodes.push(r.node)
            if (r.edge) {
              this.network.edges = this.network.edges.concat(...r.edge);
            }
          });

          this.$root.ws.on('dashboard.removeNode', r => {
            if (!_.find(this.network.nodes, r.node)) return
            this.network.nodes = this.network.nodes.filter(v => {
              return !(v.id === r.node.id)
            })
            this.network.edges = this.network.edges.filter(v => {
              return ![v.from, v.to].includes(r.node.id)
            })
          })

          this.$root.ws.on('dashboard.disconnect', () => {
            this.network.nodes = []
            this.network.edges = []
            this.$root.ws.close()
          })
        }
        else
        {
          setTimeout(() =>
          {
            this.addListeners((this.$root.ws.ready))
          }, 2000)
        }
      }
    },
    created() {
      this.addListeners(this.$root.ws.ready)
    },
    mounted() {
      this.reload();
    }
  }
</script>
