<script>

  export default {
    name: 'Transaction',
    data() {
      return {
        amount: 0,
        currency: 'ENQ',
        owner: 0,
        receiver: 0,
        timestamp: 10,
        microblock: '1'
      }
    },
    watch: {
      '$route': 'load'
    },
    methods: {
      load() {
        if (this.$root.ws.ready) {
          this.fetchData()
        } else {
          setTimeout(() => {
            this.load()
          }, 500)
        }
      },

      fetchData() {
        this.$root.ws.call('getTransaction', {
          hash: this.$route.params.id
        }).then(r => {
          console.log(r)
        _.assign(this.$data, _.pick(r, _.keys(this.$data)))
      }).catch(e => this.$router.replace({name: 'Search', params: {id: this.$route.params.id}})
      )
      }
    },
    mounted() {
      this.load()
    }
  }
</script>
